insert into [CLOUD_SYS_PARAM] ([KEY],[VALUE],[DESCRIPTION]) values ('BatchJobPollingInterval','60000','For configuring time interval (in millisecond) which all batch jobs in subscription web server will poll the DB records and create required resources on cloud.')
insert into [CLOUD_SYS_PARAM] ([KEY],[VALUE],[DESCRIPTION]) values ('MaxDBCount','50','For defining the max number of DB allowed in a DB instance on cloud. When count reached, the next DB creation request will trigger the creation of a new DB instance and update the value of "CurrentDBInstanceID" accordingly.')
insert into [CLOUD_SYS_PARAM] ([KEY],[VALUE],[DESCRIPTION]) values ('WFEPollingInterval','5000','For configuring time interval (in millisecond) which WFE batch jobs in subscription web server will send status check request to Alicloud API for checking current status of a resource during creation/configuration of that particular resource.')
insert into [CLOUD_SYS_PARAM] ([KEY],[VALUE],[DESCRIPTION]) values ('WFEPollingMaxRetryCount','10','For defining the max number of polling request for WFE batch jobs to send which the status have not changed to the desired outcome. When reached, stop sending status update request, change status to "Error" and notify admin.')